package com.ripple.core.types.ledger;

import com.ripple.core.binary.BinaryReader;
import com.ripple.core.serialized.BinaryParser;
import org.junit.Test;

import static junit.framework.TestCase.assertEquals;

public class LedgerTest {
    @Test
    /**
     * ➜  rippled git:(develop) ✗ ~/rocksdb/ldb --hex --db=/mnt/rippled/db/rocksdb get 0x498ef6300a8693c98be8345037843a95216fb254b672af679c8ef1c85d213972
     .... {some long hex string} prefixed with 0x...

     */
    public void testParsing() throws Exception {
        String hex;
        // This is the format as returned by ldb, with an `0x` prefix
        hex = "";
        hex = hex.substring(2);

        BinaryParser parser = new BinaryParser(hex);
        BinaryReader reader = new BinaryReader(parser);

        assertEquals(4403788, reader.uInt32().longValue()); //
        assertEquals(4403788, reader.uInt32().longValue()); //
        assertEquals(3, parser.readOne()); //

        LedgerHeader ledger = LedgerHeader.fromParser(parser);
        byte[] o = ledger.version.toByteArray();

        assertEquals('M',  ((char) o[0]));
        assertEquals('I',  ((char) o[1]));
        assertEquals('N',  ((char) o[2]));
        assertEquals('\0', ((char) o[3]));
    }
}
